import React, { useState, useEffect } from 'react';
import SearchBar from './components/SearchBar';
import QueryList from './components/QueryList';
import QueryModal from './components/QueryModal';
import TagFilter from './components/TagFilter';
import AddQueryModal from './components/AddQueryModal';
import {getAllQueries, saveQuery} from './components/Storage';

const App = () => {
    const [queries, setQueries] = useState([]);
    const [filteredQueries, setFilteredQueries] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedQuery, setSelectedQuery] = useState(null);
    const [tags, setTags] = useState([]);
    const [selectedTag, setSelectedTag] = useState('');
    const [showAddModal, setShowAddModal] = useState(false);

    useEffect(() => {
        const storedQueries = getAllQueries();
        setQueries(storedQueries);
        setFilteredQueries(storedQueries);

        // Extraer tags únicos de las consultas
        const allTags = [...new Set(storedQueries.flatMap(q => q.etiquetas || []))];
        setTags(allTags);
    }, []);

    const handleSaveQuery = (nuevaConsulta) => {
        const consultaGuardada = saveQuery(nuevaConsulta);
        const updatedQueries = [...queries, consultaGuardada];
        setQueries(updatedQueries);
        setFilteredQueries(updatedQueries);

        // Actualizar tags
        const allTags = [...new Set(updatedQueries.flatMap(q => q.etiquetas || []))];
        setTags(allTags);
    };

    const handleSearch = (term) => {
        setSearchTerm(term);
        filterQueries(term, selectedTag);
    };

    const handleTagSelect = (tag) => {
        setSelectedTag(tag);
        filterQueries(searchTerm, tag);
    };

    const filterQueries = (term, tag) => {
        const lowerTerm = term.toLowerCase();
        const filtered = queries.filter(query => {
            const matchesTitle = query.title.toLowerCase().includes(lowerTerm);
            const matchesContent = query.content.toLowerCase().includes(lowerTerm);
            const matchesTag = tag ? query.tags.includes(tag) : true;
            return (matchesTitle || matchesContent) && matchesTag;
        });
        setFilteredQueries(filtered);
    };

    const handleQueryClick = (query) => {
        setSelectedQuery(query);
    };

    const closeModal = () => {
        setSelectedQuery(null);
    };

    return (
        <div className="app">
            <div className="app-header">
                <h1>SQL Query Manager</h1>
                <button 
                    className="btn btn-primary add-query-btn"
                    onClick={() => setShowAddModal(true)}
                >
                    + Agregar Consulta
                </button>
            </div>

            <SearchBar onSearch={handleSearch} />
            <TagFilter tags={tags} onTagSelect={handleTagSelect} />
            <QueryList queries={filteredQueries} onQueryClick={handleQueryClick} />

            {selectedQuery && (
                <QueryModal query={selectedQuery} onClose={closeModal} />
            )}

            <AddQueryModal 
                isOpen={showAddModal}
                onClose={() => setShowAddModal(false)}
                onSave={handleSaveQuery}
            />
        </div>
    );
};

export default App;
